'use strict';

const RE_SCRIPT = /\.(?:js|jsx|coffee)$/;
const RE_STYLE = /\.(?:css|less|styl|scss|sass)$/;

module.exports = {
  escape: require('./escape'),
  isScriptFile: function(fileName) {
    return RE_SCRIPT.test(fileName);
  },
  isStyleFile: function(fileName) {
    return RE_STYLE.test(fileName);
  },
  RE_SCRIPT: RE_SCRIPT,
  RE_STYLE: RE_STYLE,
};
